import { Component } from '@angular/core';

@Component({
  selector: 'app-event-add',
  templateUrl: './event-add.component.html',
  styleUrls: ['./event-add.component.css']
})
export class EventAddComponent {
  evento: any = {};

  addEvent() {
    // Aquí puedes implementar la lógica para guardar el evento en tu base de datos
    // Puedes acceder a los datos ingresados por el usuario a través de this.evento
    // Por ejemplo: this.evento.descriptionEvent y this.evento.tipoEvento
  }
}
<div class="container">
    <h2>Agregar Evento</h2>
    <form (ngSubmit)="addEvent()" #eventoForm="ngForm">
        <div class="form-group">
            <label for="descriptionEvent">Descripción:</label>
            <input type="text" id="descriptionEvent" name="descriptionEvent" [(ngModel)]="evento.descriptionEvent" class="form-control" required>
        </div>
        <div class="form-group">
            <label for="tipoEvento">Tipo de Evento:</label>
            <select id="tipoEvento" name="tipoEvento" [(ngModel)]="evento.tipoEvento" class="form-control" required>
                <option value="1">Compra</option>
                <option value="2">Venta</option>
            </select>
        </div>
        <button type="submit" class="btn btn-primary">Guardar Evento</button>
    </form>
</div>
