import { Component, OnInit } from '@angular/core';
import { EventService } from 'src/app/event.service'

@Component({
  selector: 'app-event-list',
  templateUrl: './event-list.component.html',
  styleUrls: ['./event-list.component.css']
})
export class EventListComponent implements OnInit {
  events: any[] = [];
  originalEvents: any[] = [];
  typeFilter: number = 0;
  startDate: string = '';
  endDate: string = '';

  constructor(private eventService: EventService) {}

  ngOnInit(): void {
    this.getEvents(); // Llama a la función para cargar eventos al inicializar el componente
  }

  getEvents(): void {
    this.eventService.getEvents().subscribe((data) => {
      this.events = data; // Asigna los datos recibidos a la propiedad events
      //console.table(this.events);
      this.originalEvents = this.events;
      //console.log("this.originalEvents");
      //console.log(this.originalEvents);
    });
  }

  filterEvents(): void {
    let filteredEvents = [...this.originalEvents];
    filteredEvents = filteredEvents.filter((event) => {
      if (event.typeEvent.toString() === this.typeFilter.toString())
        return true;
      return false;
    });
    if (this.startDate && this.endDate) {
      filteredEvents = filteredEvents.filter((event) => {
        const eventD = new Date(event.dateEvent);
        eventD.setHours(0, 0, 0, 0);
        console.log(eventD)
        const startD = new Date(this.startDate);
        const endD = new Date(this.endDate);
        console.log(startD)
        console.log(endD)
        console.log(eventD >= startD)
        console.log(eventD <= endD)

        return eventD >= startD && eventD <= endD;
      });
    }

    this.events = filteredEvents;
  }

  getEventType(eventType: number): string {
    switch (eventType) {
      case 1:
        return "Venta";
      default:
        return "Compra";
    }
  }
}
<div class="filter-container">
    <label for="typeFilter">Filtrar por Tipo:</label>
    <select id="typeFilter" [(ngModel)]="typeFilter">
        <option value="0">Todos</option>
        <option value="1">Venta</option>
        <option value="2">Compra</option>
    </select>

    <label for="startDate">Fecha Inicio:</label>
    <input type="date" id="startDate" [(ngModel)]="startDate">

    <label for="endDate">Fecha Fin:</label>
    <input type="date" id="endDate" [(ngModel)]="endDate">

    <button (click)="filterEvents()">Filtrar</button>
</div>
<table>
    <tr>
        <th>Fecha</th>
        <th>Tipo</th>
        <th>Descripción</th>
    </tr>
    <tr *ngFor="let event of events">
        <td>{{ event.dateEvent | date: 'dd/MM/yyyy'}}</td>
        <td>{{ getEventType(event.typeEvent) }}</td>
        <td>{{ event.descriptionEvent }}</td>
    </tr>
</table>
