import { Component, OnInit } from '@angular/core';
import { EventService } from 'src/app/event.service'

@Component({
  selector: 'app-event-list',
  templateUrl: './event-list.component.html',
  styleUrls: ['./event-list.component.css']
})
export class EventListComponent implements OnInit {
  events: any[] = []; // Suponiendo que tienes un arreglo de eventos
  typeFilter: string = '';

  constructor(private eventService: EventService) {}

  ngOnInit(): void {
    this.getEvents(); // Llama a la función para cargar eventos al inicializar el componente
  }

  getEvents(): void {
    this.eventService.getEvents().subscribe((data) => {
      this.events = data; // Asigna los datos recibidos a la propiedad events
      console.table(this.events);
    });
  }
  getEventType(eventType: number): string {
    switch (eventType) {
      case 1:
        return "Venta";
      case 2:
        return "Compra";
      default:
        return "Venta";
    }
  }
  filterEvents(): void {
    // Aplica el filtro en función del valor de tipoFiltro
    if (this.typeFilter) {
      console.log('event:' + this.typeFilter);
      console.log(this.events);
      this.events = this.events.filter((event) =>
        event.typeEvent.includes(this.typeFilter)
      );
    } else {
      // Si el campo de filtro está vacío, restaura la lista completa
      this.getEvents();
    }
  }
}
<div class="filter-container">
    <label for="typeFilter">Filtrar por Tipo:</label>
    <select id="typeFilter" [(ngModel)]="typeFilter">
        <option value="">Todos</option> <!-- Opción para mostrar todos los eventos -->
        <option value="Venta">Venta</option>
        <option value="Compra">Compra</option>
    </select>
    <button (click)="filterEvents()">Filtrar</button>
</div>
<table>
    <tr>
        <th>Fecha</th>
        <th>Tipo</th>
        <th>Descripción</th>
    </tr>
    <tr *ngFor="let event of events">
        <td>{{ event.dateEvent | date: 'dd/MM/yyyy'}}</td>
        <td>{{ getEventType(event.typeEvent) }}</td>
        <td>{{ event.descriptionEvent }}</td>
    </tr>
</table>
