import { Component } from '@angular/core';
import { EventService } from 'src/app/event.service'

@Component({
  selector: 'app-event-add',
  templateUrl: './event-add.component.html',
  styleUrls: ['./event-add.component.css']
})
export class EventAddComponent {
  event: any = {};

  constructor(private eventService: EventService) { }

  addEvent() {
    this.eventService.saveEvent(this.event).subscribe(
      (response) => {
        console.log('Evento guardado exitosamente', response);
        // Puedes redirigir al usuario a una página de éxito o realizar otras acciones después de guardar el evento.
      },
      (error) => {
        console.error('Error al guardar el evento', error);
        // Puedes manejar el error y mostrar un mensaje al usuario en caso de fallo.
      }
    );
  }
}
<div class="form-container">
    <h2 class="form-title">Agregar Evento</h2>
    <form (ngSubmit)="addEvent()" #eventoForm="ngForm">
        <div class="form-group">
            <label for="descriptionEvent" class="form-label">Descripción:</label>
            <input type="text" id="descriptionEvent" name="descriptionEvent" [(ngModel)]="event.descriptionEvent" class="form-input" required>
        </div>
        <div class="form-group">
            <label for="typeEvent" class="form-label">Tipo de Evento:</label>
            <select id="typeEvent" name="typeEvent" [(ngModel)]="event.typeEvent" class="form-select" required>
                <option value="1">Compra</option>
                <option value="2">Venta</option>
            </select>
        </div>
        <button type="submit" class="form-button">Guardar Evento</button>
    </form>
</div>
